type StoreProps = {
  type?: string;
  name: string;
};

export function Stores({ type, name }: StoreProps): JSX.Element | null {
  switch (type) {
    case "apple-appstore": {
      return <Apple name={name} />;
    }
    case "google-play": {
      return <Google name={name} />;
    }
    case "steam": {
      return <Steam name={name} />;
    }
    case "playstation-store": {
      return <Playstation name={name} />;
    }
    case "gog": {
      return <Gog name={name} />;
    }
    case "nintendo": {
      return <Nintendo name={name} />;
    }
    case "xbox-store": {
      return <Xbox name={name} />;
    }
    case "epic-games": {
      return <Epic name={name} />;
    }
    case "itch": {
      return <Itch name={name} />;
    }
    default:
      return null;
  }
}
